insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.2.5.4', 'cae', '2.1.2.5.4', 'Instaurer un fonds local daide aux travaux via les ressources issues du dveloppement des nergies renouvelables sur le territoire ou fonds damorage grs par lEPCI', '<p>Le principe du fonds d''amorage est que les conomies dnergies ralises sur les premires oprations permettent de refinancer ce fonds pour accompagner de nouveaux projets de rnovation.</p>
', '', '', '', '', '', null, null, null);
