insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.2.6.1', 'cae', '2.1.2.6.1', 'Renforcer le suivi nergtique des btiments concerns par le plan (si la comptence gestion et suivi est dlgue, la collectivit suit activement les activits du gestionnaire et dispose dune vision de la situation existante). Le suivi est a minima annuel pour les btiments cibls par le dcret tertiaire.', '', '', '', '', '', '', null, null, null);
