insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.1.3.1.2', 'cae', '2.1.3.1.2', 'Elaborer un document "cadre" pour la qualit environnementale de ses btiments', '<p>Le document cadre inclut: rfrentiel, charte, cahier des charges modles intgrant des obligations concernant les aspects climat-air-nergie des projets de construction et de rnovation, analyse du cycle de vie via la mthodologie E+C-. Lintercommunalit invite les communes  llaboration de ce document  cadre . Pour les projets de rnovation de btiments concerns par le dcret tertiaire, le document  cadre  est cohrent avec les objectifs fixs par le dcret.</p>
', '', '', '', '', '', null, null, null);
