insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.1', 'cae', '2.2.1', 'Augmenter l''efficacit nergtique pour la chaleur et le rafrachissement des btiments publics', '<p>La collectivit met en place une dmarche de sobrit nergtique, systmatise l''efficacit nergtique, pour le chauffage, l''eau chaude et le rafrachissement de ses btiments et l''value au moyen d''indicateurs nergtiques pour diffrentes catgories de btiments.</p>
', '<p><strong>Contexte et enjeux</strong></p>
