insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.5.2.2', 'cae', '2.2.5.2.2', 'Porter une attention particulire au pouvoir de rchauffement global (PRG) des fluides frigorignes utiliss,  la qualit des oprations de maintenance (installateur qualifi, suivi des quantits de fluides recharges, rcupration des fluides, pertes minimes...) et au traitement en fin de vie de ces quipements.', '<p>Seulement pour les btiments et quipements disposant d''installations de production de froid (rfrigrateur, conglateur, climatiseur...)</p>
', '', '', '', '', '', null, null, null);
