insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.2.4.2.2', 'cae', '2.2.4.2.2', 'Vendre prioritairement  des acteurs (acheteurs, fournisseurs) proposant une offre renouvelable de haute qualit', '<p>Seulement si la production dlectricit verte est commercialise en dehors de l''obligation d''achat. Offre de haute qualit: 100% EnR, qualit environnementale, transparence, critre d''additionnalit, fond de dveloppement des EnR...</p>
