insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.2.4.4', 'cae', '2.3.2.4.4', 'Porter une attention particulire sur les piscines', '<p>Le taux de renouvellement de leau de baignade (en l/j/baigneur) est bas, les piscines sont quipes de dispositifs de destruction des chloramines et/ou de rcupration deau et des calories.</p>
