insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.3.6.1', 'cae', '2.3.3.6.1', 'Suivre de manire chiffre l''avancement de sa politique et se fixer des objectifs', '<p>Exemples : % de matriaux recycls incorpors (en tonne), % de chantiers utilisant des enrobs tides ou  froid, % de surfaces utilisant des matriaux clairs sur les nouveaux projets d''espaces publics, % de chantiers utilisant des matriaux permables (bton drainant, dalle  engazonner...).</p>
', '', '', '', '', '', null, null, null);
