insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_2.3.1.3.1', 'cae', '2.3.1.3.1', 'Mettre en uvre de mesures d''amlioration des performances issues du diagnostic', '<p>Exemples de mesures: Optimisation tarifaire, maintenance prventive, modernisation des quipements, modification/optimisation du service rendu  extinction ou variateur de tension la nuit, choix judicieux des points lumineux, gnralisation de lclairage  dtection de prsence ou encore la modration de lclairage des btiments, statues et faades...</p>
