insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.1.2.4.4', 'cae', '6.1.2.4.4', 'Commune: Elle donne lexemple au sein de lEPCI', '<p>Exprimentation dactions pilotes sur le territoire communal (ex : laurat dappel  projets de lEPCI, volontariat pour tester des outils), faire figure de rfrence auprs des autres communes membres (tmoignage frquent)...</p>
