insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.2.4.3', 'cae', '6.2.2.4.3', 'Mettre en place des actions conjointes avec les acteurs de l''hbergement pour diminuer la facture nergtique des logements et les inciter  des constructions et rnovations performantes,  recourir aux solutions nergtiques les moins onreuses', '<p>Exemple d''actions conjointes : convention/charte d''engagement/accords, contrat local dengagement CLE- convention ANRU...</p>
