insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.2.4.4', 'cae', '6.2.2.4.4', 'Utiliser les outils existants et en crer des nouveaux', '<p>par exemple : PDALPD, baux  rhabilitation, outil prventif pour l''amlioration thermique des logements, outil curatif d''aide au paiement des dettes d''nergie, Fonds de Solidarit pour le logement, Fonds Sociaux d''Aide aux Travaux de Matrise de l''Energie (FSATME), opration de distribution de &quot;kits eau/nergie&quot;...</p>
', '', '', '', '', '', null, null, null);
