insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.3.3', 'cae', '6.2.3.3', 'Accompagner le particulier jusquaux travaux de rnovation nergtique', '<p>Les ECF sont abords galement dans les mesures 1.2.4 (volet planification) et 6.2.1 (volet mobilisation des professionnels du btiment).</p>
