insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.4.2.2', 'cae', '6.2.4.2.2', 'Soutenir spcifiquement les entreprises tertiaires > 1000 m soumises aux obligations du "Dcret Tertiaire"', '<p>Decret Tertiaire: obligation de mise en uvre dactions de rduction de la consommation dnergie finale dans les btiments existants  usage tertiaire afin de parvenir  une rduction de la consommation dnergie finale pour lensemble des btiments soumis  lobligation dau moins 40 % en 2030, 50 % en 2040 et 60 % en 2050 par rapport  2010. Exemple de soutien: lettre d''information, club d''changes, visites de site, etc.</p>
', '', '', '', '', '', null, null, null);
