insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.4.6.3', 'cae', '6.2.4.6.3', 'Suivre le respect des obligations de performance du dcret tertiaire sur son territoire', '<p>par exemple : mise en place de partenariats pour obtenir les donnes au niveau local, baisse constate des consommations moyennes par type de btiment etc...</p>
