insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.1.3', 'cae', '6.2.1.3', 'Les bailleurs sociaux sont engags aux cts de la collectivit dans des actions particulirement ambitieuses', '<p>Par exemple : cration d''un rfrentiel de performance nergie-climat pour les oprations de logements sociaux, signature d''accords/de convention/d''une charte d''engagement entre la collectivit et les bailleurs, par exemple dans le cadre d''un CLE ou de programmes ANRU, co-conditionnement des garanties d''empunts accords aux bailleurs sociaux, soutien  la ralisation de diagnostic de performance nergtique dans les logements ou aux investissements cologiques (soutien technique, conseil, groupement d''achats, montage commun de dossier de certificats d''conomie d''nergie...).</p>
', '', '', '', '', '', null, 20.0, 'mise en uvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.2.1.4', 'cae', '6.2.1.4', 'Disposer dune stratgie de mobilisation des professionnels du btiment et de limmobilier formalise', '<p>La collectivit dispose dune stratgie de mobilisation des professionnels formalise, articule avec les actions des autres chelons de gouvernance (dpartement, rgion, Etat), et dote de moyens ddis (par exemple via une plateforme territoriale de la rnovation, un centre de ressources BEEP quelle soutient, etc.)</p>
', '', '', '', '', '', null, 20.0, 'mise en uvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3', 'cae', '6.3', 'Activits conomiques', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.3.3', 'cae', '6.3.3', 'Contribuer  l''innovation et l''excellence sur la thmatique climat-air-nergie', '<p>La collectivit contribue  linnovation et lexcellence sur la thmatique climat-air-nergie. Elle peut collaborer avec les centres de formation et le milieu de la recherche ou soutenir un projet phare exemplaire et ambitieux priv, dans ces mmes domaines, en apportant son expertise et/ou ses conseils pour pousser le projet plus loin dans lexcellence. Elle peut en outre favoriser lmergence de projets en fdrant les acteurs (organisation de rencontres entre collectivit, entreprises, associations et chercheurs) ou en facilitant le dpt de projets (recherche des projets, soutien mthodologique).</p>
<p>En particulier, la collectivit est cohrente dans sa prise de position  lgard des grands projets impactant en matire dmission de gaz  effet de serre, de qualit de lair ou la consommation dnergie ou lutilisation des terres agricoles sous linfluence de son territoire.</p>
', '<p><strong>Contexte et enjeux</strong></p>
<p>Le soutien  la recherche et linnovation constitue un des axes majeurs de la transition nergtique pour accompagner les filires correspondantes vers la maturit et la comptitivit, au niveau national. Grce au partenariat public-priv et  leur ancrage local, les collectivits peuvent participer, dans la mesure de leurs moyens,  linnovation et lexcellence dans le domaine climat-air-nergie.</p>
<p><strong>Rglementation</strong></p>
', '<p><strong>Communaut urbaine de Dunkerque</strong></p>
<p>Dveloppement de la filire Hydrogne</p>
<p>Dans le cadre du programme des investissements davenir pilot par lADEME, le projet de dmonstration de  Gestion des Rseaux par linjection dHydrogne pour Dcarboner les nergies  a pour objectif dvaluer en 4 ans la pertinence technico-conomique dune nouvelle filire nergtique compose de mlange dhydrogne et de gaz naturel sur diffrents marchs.</p>
<p>Soutenues par des actions de recherche industrielle, deux dmonstrations de production, distribution et utilisation locale dhydrogne,  terme dorigine renouvelable, seront ralises sur le territoire de la Communaut Urbaine de Dunkerque :</p>
<p> volet  Habitat/logement  : injection dhydrogne dans le rseau gaz naturel (entre 6 % et 20 %) pour satisfaire les besoins de chaleur de 200 nouveaux logements</p>
<p> volet  Transport  : carburant Hythane (80 % de gaz naturel et 20 % dhydrogne). Utilis dans une cinquantaine de bus existants fonctionnant au GNV, il permet, moyennant des adaptations rapides et  moindre cot, dintroduire de lhydrogne pour diminuer de 7 % les missions de CO2 et de 10 % celles des NOx.</p>
<p> Source : dossier de candidature Citergie GOLD, 2013 + recueil de BP Citergie</p>
<p><strong>CA Chalon-sur-Sane</strong></p>
<p>Soutenir des projets locaux dconomie circulaire</p>
<p>Le Grand Chalon a soutenu le dveloppement dune filire locale de fabrication dun isolant compos de ouate de cellulose issu de chutes de carton, mis en place par une entreprise dinsertion par lactivit conomique. La seconde tape  mettre en uvre est la rcupration des cartons issus de la collecte ralise par le Grand Chalon auprs de ses commerants pour les intgrer dans le process de fabrication.</p>
<p>Lisolant a obtenu dernirement les certifications ACERMI et CQMB, ainsi le Grand Chalon peut cibler cet isolant dans les consultations pour les travaux disolation des btiments publics.</p>
<p> Source : dossier de candidature Cap Citergie, 2016 + recueil de BP Citergie</p>
', '', '<p>Les relations noues avec les centres de formation ou de recherche situs en dehors du territoire sont pris en compte.</p>
<p>Lien avec laction 6.3.1  Favoriser les activits conomiques durables et valoriser les ressources locales </p>
', '<p>Pas de rduction de potentiel</p>
', 6.0, null, null);
