insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.2.3.2', 'cae', '6.4.2.3.2', 'Raliser un diagnostic de la fort', '<p>Inlucre dans le diagostic les surfaces par typologie des forts, les stocks de carbone et la squestration de carbone par la fort, le taux des prlvements, la rpartition de la rcolte entre les diffrents usages du bois, de ses usages et de ses acteurs sur le territoire.</p>
