insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.2.3', 'cae', '6.4.1.2.3', 'Mettre en relation les acteurs', '<p>par exemple : producteurs-consommateurs-restaurateurs, commissions locales  linstallation  caf installation-, limitation du gaspillage alimentaire, facilitation et accompagnement dassociations daide alimentaire, don, acteurs de lalimentation, de la sant et du social autour des questions daccs de tous  une alimentation saine et durable </p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.3', 'cae', '6.4.1.3', 'Amliorer la performance environnementale de la production agricole', '<p>Les amliorations visent les champs suivants :</p>
<ul>
<li>
<p>la diminution des missions de gaz  effet de serre de lagriculture (diminuer lutilisation des engrais azots de synthse, encourager la culture de lgumineuses, amliorer lautonomie alimentaire des levages, meilleur stockage des lisiers, amlioration de la prise en compte de la qualit des sols, etc.)</p>
</li>
<li>
<p>lefficacit nergtique sur les systmes techniques (passage au banc dessai des tracteurs, mesures particulires pour limiter la consommation dnergie de la production laitire, des serres agricoles et le schage, dveloppement dnergies renouvelables dans les btiments agricoles)</p>
</li>
<li>
<p>volution des pratiques agricoles vers lamlioration de la qualit des sols et laugmentation de la squestration carbone (fraction organique, rserve utile, rotations des cultures longues et  espces multiples, maintien des prairies, maintien et plantation des haies, agroforesterie) par le biais par exemple de la formation/sensibilisation</p>
</li>
<li>
<p>la prservation des ressources en eau (espces peu gourmandes en eau et amlioration des techniques dirrigation)</p>
</li>
<li>
<p>la limitation des missions dammoniac et de particules (levage et culture, par exemple :  diminuer lutilisation des engrais azots, encourager la culture de lgumineuses, favoriser les appareils dpandage plus performants et moins propices  la volatilisation des particules, encourager la mise en place de bonnes pratiques agricoles faisant consensus (Guide de bonnes pratiques agricoles, BREF elevage/ MTD, )</p>
</li>
</ul>
', '', '', '', '', '', null, 15.0, 'mise en uvre');
