insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.1', 'cae', '6.4.1.5.1', 'Revoir les approvisionnements en restauration collective publique', '<p>A minima 50% de produits durables et de qualit, dont au moins 20% issus de lagriculture biologique (critres Egalim) ; privilgier les produits locaux ou ancrs dans les territoires (PAT); servir uniquement des fruits et lgumes frais de saison, favoriser les produits issus du commerce quitable.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.2', 'cae', '6.4.1.5.2', 'Informer les convives sur lorigine, la saisonnalit et la qualit des produits servis (Egalim et autres critres de qualit / durabilit); mettre en place un affichage environnemental et nutritionnel des menus', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.3', 'cae', '6.4.1.5.3', 'Diversifier les apports alimentaires, en cohrence avec les nouvelles recommandations de Sant Publique France', '<p>Notamment : mettre en place un plan de diversification protique, introduisant notamment les lgumineuses (obligatoire  partir de 200 couverts/j, Egalim) ; proposer au moins un menu vgtarien hebdomadaire (obligatoire en restauration scolaire, Egalim) et/ou une alternative vgtarienne (choix multiples).</p>
', '', '', '', '', '', null, null, null);
