insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.6', 'cae', '6.4.1.5.6', 'Former les cuisinieres et le personnel de cantine  l''introduction de produits bio locaux et  la diversification des protines, et plus globalement aux enjeux environnementaux, et leur fournir un corpus de recettes adaptes  la restauration collective. Les former galement aux dmarches de rduction du gaspillage alimentaire.', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.7', 'cae', '6.4.1.5.7', 'Former les acheteurses et les gestionnaires impliques dans la restauration collective aux questions environnementales, sociales, sanitaires de lalimentation, aux achats bios/locaux et aux dmarches de rduction du gaspillage alimentaire.', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.5.8', 'cae', '6.4.1.5.8', 'Mener des actions spcifiques en restauration scolaire', '<p>En lien avec la mesure 6.5.4</p>
<p>Organiser des ateliers sur la provenance des aliments consomms par les enfants (dont les protines animales), sur l''impact environnemental de leur assiette, pour les produits locaux ou dimportation faisant partie de nos habitudes alimentaires</p>
<p>Organiser des visites de fermes do proviennent les aliments cuisins ou des interventions des producteurs</p>
<p>Organiser avec les enfants des peses dassiette, et le tri slectif des dchets, pour agir sur le gaspillage alimentaire</p>
<p>Co-construire les menus avec euxelles (et lea ditticienne de la ville)</p>
', '', '', '', '', '', null, null, null);
