insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.6.2', 'cae', '6.4.1.6.2', 'Acqurir, mettre en rserve ou mettre  disposition du foncier en partenariats avec des associations (exemple Foncire Terre de Liens)', '<p>Mettre en rserve via une convention de portage foncier EPF ou SAFER ou un stockage en direct par la collectivit</p>
<p>Mettre  disposition du foncier (terres ou btiment, fermes communales ou intercommunales, fermes relais, rgies communales)</p>
