insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.4.1.7', 'cae', '6.4.1.7', 'Adopter une vision globale grce  la formalisation dun Projet Alimentaire Territorial (PAT)', '<p>Formaliser son PAT (par exemple via la reconnaissance au niveau national par le Ministre de lagriculture et de lalimentation) avec des critres cologiques et sociaux, compatible avec le PCAET, prenant en compte la rsilience alimentaire et appuyant la structuration de filires paysannes, biologiques et locales, rmunratrices pour les agriculteurs.</p>
