insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_6.5.2.3', 'cae', '6.5.2.3', 'Sensibiliser les habitants sur lco-consommation et les co-gestes', '<ul>
<li>
