insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.3.1', 'cae', '3.1.1.3.1', 'Pour les rseaux de chauffage/froid urbain, tre attentif  la performance environnementale dans le choix de son concessionnaire et inscrire des exigences dans le contrat et les avenants qui les lient', '<p>Exemple dexigences : ralisation des travaux et conseils en MDE auprs de la collectivit et des usagers, tudes de potentiel et promotion des nergies renouvelables, mise en place dun fond ddi  lamlioration de performance nergtique du rseau, systme de management environnemental type ISO, suivi dun indicateur en gCO2/kWh... La ralisation effective des actions est vrifie via le rapport annuel du concessionnaire. Des indicateurs de performance nergtique, environnementale et conomique du rseau de chaleur sont mis en vidence  travers un suivi mensuel ou a minima un bilan annuel.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.3.2', 'cae', '3.1.1.3.2', 'Demander aux concessionnaires de prciser dans leurs rapports annuels les mesures d''amlioration des performances nergtiques ralises et l''tat des rflexions et actions sur les smart grid ou linjection du bio-mthane dans le rseau gaz', '<p>Exemples de mesures d''amlioration des performances nergtiques: transformateurs performants, calorifugeage des rseaux de chaleur, mesures de rendement...</p>
<p>Exemples d''tat sur les smart grid: tat du dploiement des compteurs Linky et Gazpar, utilisation des donnes issues des compteurs communicants, opportunits et besoins de flexibilit, rflexion sur l''intgration de productions EnR lectriques dcentralises, rflexion sur l''intgration des vhicules lectriques et ses rpercussions sur les rseaux...</p>
', '', '', '', '', '', null, null, null);
