insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.4.1', 'cae', '3.1.1.4.1', 'Dfinir un plan stratgique / une feuille de route propre  la collectivit', '<p>Il doit tre aliment par les rapports annuels des concessionnaires, suivi dans le temps par des agents forms. Les solutions organisationnelles et techniques permettant le soutien au dveloppement des nergies renouvelables lectriques sur le territoire en minimisant les couts de raccordement pour les producteurs sont par exemple tudies.</p>
', '', '', '', '', '', null, null, null);
