insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.1.1.5.1', 'cae', '3.1.1.5.1', 'Oeuvrer (via lautorit concdante le cas chant) pour la mise en place d''un service de flexibilit local auprs du gestionnaire de rseau (tude en cours ou service en place), appel galement  boucle nergtique locale  ou quivalent', '<p>Montage dune action pilote de MDE sur une zone particulire, en coordonnant tous les intervenants, en premier lieu le Syndicat dEnergie et le gestionnaire de rseau lectrique.</p>
<p>Exemple de contenu : donnes de consommation des btiments (agrges  diffrentes chelles : IRIS, rue, )  et rseaux fournies par les gestionnaires des rseaux, croisement des donnes nergie avec les donnes du parc de btiments via SIG pour identifier les zones d''actions prioritaires et dfinir des indicateurs de suivi pertinents, conseils tudes et incitations grand-public, actions commerciales des fournisseurs orientes MDE grce aux CEE, actions cibles sur les zones o le rseau est en contrainte, montage de dossiers FACE de type macro</p>
', '', '', '', '', '', null, null, null);
