insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.2.3.2.1', 'cae', '3.2.3.2.1', 'Susciter les projets citoyens et/ou encadrer lintervention dun dveloppeur extrieur pour raliser et exploiter un quipement ddi  la production dlectricit', '<p>en complment des installations publiques (action 2.2.3), lancer des appels doffres dans lesquels les conditions prcises du dveloppement et de lexploitation du site sont dtermines, mettre en place des partenariats publics-privs...</p>
<p>Equipement ddi: un parc olien, une ferme solaire, une unit de biomasse ou une centrale hydraulique :</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.2.3.2.2', 'cae', '3.2.3.2.2', 'La puissance photovoltaque installe sur le territoire vise des seuils (dans la description)', '<p> pour les collectivits &gt; 100 000 habitants : 20 Wc/hab (Mtropole) ; 60 Wc/hab (DOM-ROM)</p>
<p> pour les collectivits &gt; 50 000 habitants : 40 Wc/hab (Mtropole) ; 120 Wc/hab (DOM-ROM)</p>
<p> pour les collectivits &lt; 50 000 habitants : 60 Wc/hab (Mtropole) ; 180 Wc/hab (DOM-ROM)</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.2.3.2.3', 'cae', '3.2.3.2.3', 'Mettre en fonctionnement des installations oliennes ou hydrolectriques sur le territoire', '<p>Si le potentiel existe, avec y compris l''installation de micro-olien ou micro-hydrolectricit</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.2.3.3', 'cae', '3.2.3.3', 'Atteindre une part leve dlectricit renouvelable', '<p>La production d''lectricit renouvelable atteint:</p>
<ul>
<li>
<p>16% de la consommation d''lectricit (en nergie finale) pour les territoires ne disposant pas de potentiel olien ou hydraulique (attest par une tude)</p>
</li>
<li>
<p>40% pour les territoires disposant dun potentiel olien ou hydraulique (attest par une tude ou des installations existantes)</p>
</li>
<li>
<p>75% dans les DOM-ROM (rappel : l''objectif vis est l''autonomie nergtique dans les DOM en 2030)</p>
</li>
</ul>
<p>En prsence de rgie ou SEM lectriques : Le mix nergtique propos par les structures atteint 40% (Mtropole) et 100% (DOM-ROM) dlectricit renouvelable.</p>
<p>Nota : En prsence d''une rgie/SEM, 30% des points sur le premier critre, 20% sur le deuxime.</p>
', '', '', '', '', '', null, 50.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.2.2', 'cae', '3.2.2', 'Augmenter l''utilisation des nergies renouvelables pour la chaleur et le rafrachissement', '<p>Le potentiel d''utilisation des nergies renouvelables pour le chauffage des btiments, l''eau chaude sanitaire et le rafrachissement est puis, avec une attention sur la qualit de l''air. Lorsque cela est possible, des rseaux de chaleur renouvelable sont mis en place. Latteinte des objectifs est mesure avec le pourcentage dnergie d''origine renouvelable dans la consommation de chaleur ou de  sur le territoire.</p>
', '<p><strong>Contexte et enjeux</strong></p>
<p>En France mtropolitaine, la production de chaleur reprsente la moiti des consommations dnergie finale. Elle repose encore principalement sur les combustibles fossiles, alors que notre pays ne manque pas dalternatives (solaire, biomasse, biogaz, gothermie, etc).</p>
<p><strong>Rglementation</strong></p>
<ul>
<li>
<p>Loi n 2015-992 du 17 aot 2015 relative  la transition nergtique pour la croissance verte (TECV): La France sengage  porter la part des nergies renouvelables  au moins 32 % de sa consommation dnergie finale dici  2030 (38% de la consommation finale de chaleur) et  multiplier par 5 la quantit de chaleur et de froid livre par les rseaux de chaleur et de froid livre par les rseaux de chaleur et de froid dici 2030. La France sengage galement  parvenir  lautonomie nergtique dans les dpartements doutre-mer  lhorizon 2030 (objectif intermdiaire, 50 % dnergies renouvelables  lhorizon 2020).</p>
</li>
<li>
<p>Loi nergie-Climat du 9 novembre 2019 Article 35: Possibilit dimplanter par drogation des installations de production dnergie renouvelable dans les zones de prvention des risques technologiques.</p>
</li>
<li>
<p>Loi nergie-Climat du 9 novembre 2019 Article 40: Cration des communauts dnergies renouvelables, qui constituent un nouvel outil pour dvelopper des projets ports par des citoyens ou des collectivits locales, et extension du rgime de lautoconsommation collective.</p>
</li>
<li>
<p>Loi nergie-Climat du 9 novembre 2019 Articles 31 et 32: Scurisation juridique des procdures environnementales dautorisation des projets dnergies renouvelables et des plans et programmes soumis  valuation environnementale.</p>
</li>
</ul>
', '<p><strong>CC du Thoursais</strong></p>
<p>Parc des Technologies Innovantes pour la Production dnergies Renouvelables (TIPER)</p>
<p>Le projet TIPER est port depuis 2003 par la Communaut de communes du Thouarsais. Il vise  reconvertir un ancien site militaire, en y dveloppant un ple li aux nergies renouvelables  l''chelle industrielle dans le Thouarsais. Ce parc comprend:</p>
<ul>
<li>
<p>1 unit de mthanisation, dune puissance de 3 MW;</p>
</li>
<li>
<p>3 parcs solaires, de 15, 16 et 21 ha, dune puissance totale de 28,2MWc (2 parcs sont en fonctionnement, le dernier est en cours de dveloppement);</p>
</li>
<li>
<p>2 parcs oliens, dune puissance totale de 12,9MW: lun fonctionne, le second est en cours de dveloppement avec un financement participatif;</p>
</li>
<li>
<p>1 unit de gazification (CHO TIPER), en cours de dveloppement (chantier septembre 2017);</p>
</li>
</ul>
<p>En 2016 :</p>
<ul>
<li>
<p>33 % des consommations thermiques du territoire taient couvertes par des nergies renouvelables</p>
</li>
<li>
<p>54 % des consommations lectriques du territoire taient couvertes par des nergies renouvelables.</p>
</li>
</ul>
<p>Source: dossier de candidature Citergie, 2017</p>
<p><strong>Dijon Mtropole</strong></p>
<p>Un rseau de chaleur urbain, aliment par des nergies renouvelables et en extension</p>
<p>La mtropole a la progression la plus importante en rseau de chaleur urbain (RCU) de France. En 2019, le rseau stend sur 104 km (pour 24 km en 2014) et fournit 380 GWh de chaleur. Le rseau continue de stendre pour atteindre 120 km et 550 GWh en 2023. Dijon mtropole aura ainsi le troisime rseau franais (hors Paris) en matire de taille et de chaleur dlivre. Le RCU fonctionne grce aux 2 chaufferies biomasse construites par Dijon mtropole et  lUnit de Valorisation nergtique (UVE). Il permet ainsi dviter lmission de 38 500 tonnes deCO2, soit lquivalent de 16 000 vhicules. Lobjectif est de desservir le maximum de consommateurs avec des nergies renouvelables pour un cot modr.</p>
<p>Source: Fiche collectivit laurate, 2019</p>
<p><strong>Niort</strong></p>
<p>Chaufferie bois des Brizeaux</p>
<p>La chaufferie bois des Brizeaux dessert grce  un rseau de chaleur, plusieurs tablissements publics du quartier: un groupe scolaire, une crche, une maison de retraite, des logements sociaux individuels ou collectifs. Ces btiments rejettent 95 tonnes de CO
<sub>
2
</sub>
par an contre 385 tonnes auparavant. Lapprovisionnement de la chaufferie s''effectue  partir de plateformes locales et reprsente ainsi un intrt conomique pour la filire du bois existante autour de Niort.</p>
<p> Source: dossier de candidature CAP Citergie, 2013 et 2018</p>
', '<p>Fonds Chaleur, ADEME:</p>
<a href="https://www.ademe.fr/expertises/energies-renouvelables-enr-production-reseaux-stockage/passer-a-laction/produire-chaleur/fonds-chaleur-bref">
https://www.ademe.fr/expertises/energies-renouvelables-enr-production-reseaux-stockage/passer-a-laction/produire-chaleur/fonds-chaleur-bref
</a>
<p>Voir aussi la mesure 1.2.1.</p>
', '<p>En labsence de potentiel de dveloppement des rseaux de chaleur, la collectivit est value uniquement sur la couverture des besoins du territoire par les nergies renouvelables (effets). Pour les DOM-ROM, la collectivit est value uniquement sur ce point.</p>
<p>Cette action est axe sur la mise en uvre et les effets de la programmation nergtique territoriale visant  augmenter lutilisation des nergies renouvelables pour la chaleur et le rafrachissement  lchelle du territoire :</p>
<ul>
<li>
<p>Ne pas valuer les tudes de potentiel de dveloppement des nergies renouvelables et la dfinition du schma directeur de rseau de chaleur, value dans laction (1.2.1)</p>
</li>
<li>
<p>Ne pas valuer la chaleur issue de la cognration fossile (3.2.1)</p>
</li>
<li>
<p>Ne pas valoriser les rseaux de chaleur 100% fossiles</p>
</li>
</ul>
<p>Le primtre de laction comprend :</p>
<ul>
<li>
<p>la chaleur renouvelable produite selon les filires cites dans le Dcret n 2016-849 du 28 juin 2016  relatif au plan climat-air-nergie territorial :  biomasse  solide,  pompes    chaleur,  gothermie,  solaire  thermique,  biogaz. Par convention, 50% de lnergie produite par lincinration des dchets est considre issue de dchets urbains renouvelables (source DGEC, dans ses bilans). Les pompes  chaleur prise en compte sont les pompes  chaleur eau/eau, sol/eau, sol/sol  avec une efficacit nergtique  126 % (PAC basse temprature) et une efficacit nergtique  111 % (PAC moyenne ou haute temprature) (exigences du crdit dimpt pour la transition nergtique 2018).</p>
</li>
<li>
<p>la chaleur de rcupration distribue par les rseaux de chaleur (uniquement dans l''indicateur taux dnergie renouvelable et de rcupration des rseaux de chaleur)</p>
</li>
<li>
<p>les btiments de la collectivit (tudis seuls dans la mesure 2.2.3)</p>
</li>
<li>
<p>les initiatives prives</p>
</li>
</ul>
<p>Labellisation unique ville-EPCI : Des indicateurs distincts peuvent tre suivis ou gards en mmoire par les deux collectivits mais la notation Cit''ergie est base sur les indicateurs de lensemble du territoire intercommunal.</p>
', '<p>Pas de rduction de potentiel</p>
', 12.0, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.2.2.1', 'cae', '3.2.2.1', 'Utiliser les rseaux de chaleur pour atteindre les objectifs fixs en matire de chaleur renouvelable', '<p>Le taux de production dnergie renouvelable et de rcupration des rseaux de chaleur (hors rseaux 100% fossiles) doit atteindre 75%.</p>
<p>Le taux de couverture des besoins en chaleur (pour les secteurs rsidentiel et tertiaire) par les rseaux de chaleur dnergies renouvelables et de rcupration (hors rseaux 100% fossiles) doit atteindre 10%.</p>
', '', '', '', '', '', null, 50.0, 'mise en uvre');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.2.2.2', 'cae', '3.2.2.2', 'Atteindre les objectifs fixs en matire de couverture des besoins de chaleur et de rafraichissement du territoire par les nergies renouvelables', '<p>Le taux de production d''nergie renouvelable pour la chaleur et le rafrachissement sur le territoire doit atteindre 38% dnergies renouvelables en Mtropole, et doit atteindre 75% de la couverture des besoins par les nergies renouvelables dans les DOM (rappel : l''autonomie nergtique est vise  l''horizon 2030). Valorisation proportionnelle  partir de 10%.</p>
<p>Pour les DOM-ROM, la prsence dun rseau de froid est bonifie (+10%). La prsence dun rseau de froid aliment en partie par des nergies renouvelables ou de rcupration est davantage bonifie (+20%).</p>
', '', '', '', '', '', null, 50.0, 'effets');
