insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.4.7', 'cae', '3.3.4.7', 'Utiliser des outils de contractualisation oprationnels pour la mise en uvre de la trame verte et bleue', '<p>La collectivit met en place/promeut des outils de contractualisation et de matrise foncire transversaux dans lesquels la &quot;prservation de la biodiversit, la protection des milieux et des ressources&quot; constitue un pilier majeur.</p>
