insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.7', 'cae', '3.3.5.2.7', 'Promouvoir les bonnes pratiques de compostage auprs des particuliers', '<p>Pour les collectivits pratiquant prioritairement le compostage, elles veillent : promouvoir les bonnes pratiques auprs des particuliers (formation de matres composteurs, distribution de fourche ou brasse-compost, aires de dmonstration, rappel des paramtres cls : quilibre carbone/Azote, taux d''humidit et aration). Pour les plateformes de compostage centralis, les meilleures pratiques sont ralises et formalises (courbes de suivi des tempratures et de l''humidit, aration...), l''impact des dplacements engendrs pour l''acheminement des dchets verts est pris en compte (choix de la localisation...) et le compost produit est valoris localement et est conforme  la norme (analyses physico-chimiques). En milieu rural, du co-compostage  la ferme avec un ou plusieurs agriculteurs volontaires est ralis.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.8', 'cae', '3.3.5.2.8', 'Contrler l''interdiction du brlage  l''air libre des dchets verts et mener de nombreuses actions sur le sujet', '<p>Des actions de sensibilisation sont menes.</p>
<p>Des solutions alternatives sont proposes aux particuliers (amlioration de la collecte des dchets verts, travail sur le compostage dans la restauration collective, filire courte de valorisation pour alimenter les chaufferies bois...).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.2.9', 'cae', '3.3.5.2.9', 'Trouver des dbouchs pour l''ensemble de l''nergie produite et pour le compost', '<p>par exemple : vente  un fournisseur de gaz, raccordement  un rseau de chaleur, favoriser linstallation dutilisateurs dnergie  proximit des sites de traitements des dchets producteurs dnergie ), etc - pour l''nergie - en lien avec le secteur agricole - pour le compost -.</p>
', '', '', '', '', '', null, null, null);
