insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.3.2', 'cae', '3.3.5.3.2', 'Les centres de stockage des dchets mnagers et assimils font l''objet d''une valorisation nergtique du biogaz >75%', '<p>par exemple : production de chaleur, cognration ou injection dans le rseau gaz. La valorisation est proportionnelle selon le pourcentage de biogaz valoris,  partir de 75% et avec le maximum des points  100%.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.5.3.3', 'cae', '3.3.5.3.3', 'Augmenter la production dlectricit et de chaleur en kWh produit  partir de biodchets pour l''ensemble du territoire (mnages et activits conomiques, agricoles...) ou  dfaut la quantit en kg/hab.an de biodchets mnagers et assimils issus de collecte slective valoriss', '', '', '', '', '', '', null, null, null);
