insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.1.2', 'cae', '3.3.2.1.2', 'Demander dans les contrats de DSP la recherche de lefficacit nergtique des stations d''puration ou l''inscrire dans la stratgie de la rgie', '<p>Une certification ISO 14001 et/ou 50001, etc. du service ou des installations est un plus.</p>
