insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.2', 'cae', '3.3.2.2', 'Analyser le potentiel de rcupration de chaleur sur les eaux uses', '<p>Le potentiel d''utilisation des calories  des stations dpuration et des collecteurs d''eaux uses est dtermin, une tude existe. Elle doit croiser les tailles de collecteurs, les dbits disponibles avec les possibilits dutilisation (les sites consommateurs sont identifis).</p>
', '', '', '', '', '', null, 10.0, 'bases');
