insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.3.1', 'cae', '3.3.2.3.1', 'Raliser une tude sur les possibilits de valorisation des boues', '<p>La valorisation matire et la valorisation nergtique doivent tre tudies de manire complmentaire selon les dbouchs et les possibilits techniques. Le co-traitement avec les dchets mnagers et/ou dorigine agricole est  tudier pour de faibles volumes.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.3.2', 'cae', '3.3.2.3.2', 'Etre attentif  l''actualisation des donnes et conclusions de l''tude sur les possibilits de valorisation des boues', '<p>Les gisements et techniques pouvant voluer au fil du temps, la collectivit doit tre attentive (attention aux tudes trop anciennes).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.4', 'cae', '3.3.2.4', 'Raliser les travaux damlioration de lefficacit nergtique du systme dassainissement', '<p>Exemples d''actions possibles pour amliorer lefficacit nergtique sur les rseaux :</p>
<ul>
<li>
<p>rseaux sparatifs (hors centres villes anciens),</p>
</li>
<li>
<p>campagnes de vrification des conformits des branchements et de diminution des eaux claires parasites</p>
</li>
<li>
<p>campagnes de connaissance des tats des collecteurs visitables et non visitables et programmation de travaux</p>
</li>
<li>
<p>optimisation des postes de relvement (conditions hydrauliques, fonctionnement)</p>
</li>
<li>
<p>systmes de stockage et dpollution des eaux pluviales (valu dans la mesure 3.3.3 ddie)</p>
</li>
</ul>
<p>Exemples daction possibles pour amliorer lefficacit nergtique des stations dpuration :</p>
<ul>
<li>
<p>remplacement / optimisation du rseau d''aration des traitements biologiques, et mise en place dune rgulation  asservie  la charge  traiter (et non cadence - dure ou potentiel REDOX),</p>
</li>
<li>
<p>mise en place de variateurs de vitesse selon pertinence</p>
</li>
<li>
<p>Filire pluviale ddie visant  limiter la dilution de leffluent</p>
</li>
</ul>
<p>Nota : notation variable selon le % des stations dpuration couvertes par des mesures doptimisation nergtique ( moduler aussi en fonction du volume deau trait par ces stations).</p>
', '', '', '', '', '', null, 20.0, 'mise en uvre');
