insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.5.2', 'cae', '3.3.2.5.2', 'Mettre en place un dispositif de valorisation des boues dpuration et dpasser le simple respect de la rglementation dans sa mise en uvre', '<p>Exemplees: mthanisation avec injection du biogaz dans le rseau ou cognration si dbouchs de la chaleur existantes  proximit (le chauffage des digesteurs et des locaux ne suffit pas, il est possible dutiliser la chaleur de leau pure pour cela), valorisation matire avec optimisation de la solution choisie ou caractre innovant montrant une rflexion pousse de la collectivit (schage solaire des boues, traitements visant  amliorer l''intrt agronomique des boues ou leur manutention, compostage la ralisation du plan dpandage rglementaire est apprci mais ne suffit pas pour avoir lensemble des points).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.6', 'cae', '3.3.2.6', 'Suivre rgulirement les actions et valuer leurs effets', '<p>Par dfaut : ratio kWh/m3 pur en diminution (comparaison sur plusieurs annes).</p>
<p>Ces indicateurs sont rgulirement utiliss pour des actions correctives et d''amlioration.</p>
<p>Nota : 20% des points sur le premier critre, 5% sur les autres.</p>
', '', '', '', '', '', null, 30.0, 'effets');
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.2.6.1', 'cae', '3.3.2.6.1', 'Suivre l''effet des mesures pour amliorer lefficacit nergtique des systmes dassainissement par des indicateurs', '<p>Indicateurs prioritaires :</p>
<ul>
<li>
<p>ratio kWh/kgDBO5 limin se situe, selon filire, autour des valeurs suivantes : boues actives entre 2 et 4</p>
</li>
<li>
<p>SBR (racteur biologique squenc) autour de 4</p>
</li>
<li>
<p>BRM (bioracteur  membranes) autour de 5.</p>
</li>
</ul>
', '', '', '', '', '', null, null, null);
