insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_3.3.3.5.1', 'cae', '3.3.3.5.1', 'Participer activement  l''laboration d''une politique de gestion des inondations', '<p>Seulement si la collectivit est concerne, notamment dans la perspective de l''adaptation au changement climatique (chelle communale ou supra-communale, par exemple via les programmes d''actions de prvention contre les inondations (PAPI), le plan submersions rapides...).</p>
