insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.1.1', 'cae', '4.1.1.1.1', 'Raliser une enqute destine  valuer la part modale de chaque mode de dplacement actif ou alternatif', '<p>Ralisation d''une Enqute Mobilit Certifie CEREMA (EMC) ou d''une mthodologie simplifie ou partenariale pour les petites collectivits (niveau intercommunal avec dclinaison communale, tude ralise dans le cadre de l''laboration d''un PDM, d''un SCoT ou d''un PLU...). A minima, pour les territoires ruraux, les parts modales des trajets domicile-travail  partir des donnes INSEE sont identifies.</p>
', '', '', '', '', '', null, null, null);
