insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.2.3', 'cae', '4.1.1.2.3', 'Travailler a minima avec les autres collectivits et les Autorits Organisatrices des Mobilits afin de dvelopper lintermodalit (promotion des autres modes de transports disponibles au dpart ou  destination, etc.)', '<p>Les AOM sont comptentes en matire dorganisation des dplacements sur leur territoire.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.3', 'cae', '4.1.1.3', 'Promouvoir lintermodalit et les modes de transports alternatifs  la voiture individuelle sous des formes et vers des cibles varies', '<p>La collectivit utilise pour cela des supports et des formats varis :</p>
<ul>
<li>
<p>support d''informations papier (plan, affiche, presse) et internet (plateforme de co-voiturage, calculateur ditinraires, );</p>
</li>
<li>
<p>organisation d''vnements ayant pour sujet l''co-mobilit (journe sans voiture, challenge de la mobilit,  animations festives autour du vlo, essai de vlos  assistance lectrique,  vlorution ...);</p>
</li>
<li>
<p>livret dinformation, plan thmatique et  offres dcouvertes  pour les nouveaux arrivants;</p>
</li>
<li>
<p>conseils en mobilit lors des nouvelles zones d''amnagement urbain ou de nouvelles constructions;</p>
</li>
<li>
<p>soutiens financiers varis;</p>
</li>
<li>
<p>mise en oeuvre damnagements urbains temporaires, en utilisant les principes de concertation de lurbanisme tactique, pour tester de nouveaux amnagements encourageants les modes alternatifs en vue de les prenniser si ncessaire.</p>
</li>
</ul>
<p>Les actions spcifiques  destination des scolaires sont values dans la mesure 6.5.3.</p>
', '', '', '', '', '', null, 15.0, 'mise en uvre');
