insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.4.1', 'cae', '4.1.1.4.1', 'Centraliser linformation disponible dans un lieu ddi  la mobilit ou diffuse par un service unique', '<p>Ce lieu/service doit tre facilement identifiable par les usagers (numro dappel ddi, horaires douverture larges, localisation centrale). Ce lieu/service, qui peut tre mutualis  lchelle intercommunale, dispense des conseils gratuits et vise  rduire lutilisation systmatique de la voiture individuelle. Il s''adresse aux entreprises, aux administrations, aux commerces, aux tablissements d''enseignements, de soins ou de loisirs, mais aussi aux particuliers (salaris, lves, parents, touristes, clients...).</p>
', '', '', '', '', '', null, null, null);
