insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.5', 'cae', '4.1.1.5', 'Travailler avec les acteurs de la mobilit  lchelle du bassin de vie', '<p>La collectivit cr ou participe  un rseau local ddi  la mobilit durable. Ce rseau est largi et regroupe les diffrents acteurs locaux concerns par la mobilit durable (collectivits, services de l''tat, AOT-AOM, entreprises de transport, entreprises, organismes consulaires, associations d''usagers ou de salaris, etc.). Le rseau est destin  capitaliser et mutualiser les bonnes pratiques ainsi que les moyens (plans de mobilit , plateforme de covoiturage, formations  l''co-conduite, etc.). Il contribue galement  la finalisation et l''adaptation de la stratgie en matire de transport et de mobilit, notamment pour l''laboration d''un plan de mobilit ou dun plan de mobilit simplifi  (cf. action 1.2.2).</p>
', '', '', '', '', '', null, 10.0, 'mise en uvre');
