insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.1.1.6.1', 'cae', '4.1.1.6.1', 'Analyser la progression des parts modales dans le temps et les comparer avec les parts modales moyennes  l''chelle nationale (ou rgionale pour les DOM) ou encore de collectivits exemplaires', '<p>Par exemple : part modale des pitons, des cyclistes, des TC, du co-voiturage ou autre systme alternatif  la voiture individuelle, adapt au milieu rural notamment (cf. indicateurs).</p>
<p>Pour les collectivits rurales ou de petite taille, ce suivi peut-tre mutualis avec dautres collectivits, et effectu  une chelle suprieure (avec appropriation et dclinaison par la collectivit infra).</p>
', '', '', '', '', '', null, null, null);
