insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.2.1', 'cae', '4.3.3.2.1', 'Se rapprocher des AOM (autorits organisatrices de la mobilit) locales afin d''valuer les besoins des usagers actuels', '<p>Celles-ci pourront fournir des lments de diagnostic concernant : l''usage des transports collectifs (frquentation des points d''arrt en semaine et le week-end, origine et destination des usagers, types de titres de transport utiliss, difficults rencontres (en particulier sur les heures de pointe), l''offre de transport (type et nombre de vhicules en exploitation), les lments contractuels (type de conventionnement, date du prochain conventionnement, montant de la subvention d''quilibre, recettes tarifaires), l''historique des ventuels dysfonctionnements (horaires non ajusts, retards, sous-capacit ou surcapacit des vhicules), la fiabilit lors d''vnements climatiques extrmes.</p>
', '', '', '', '', '', null, null, null);
