insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.4.1', 'cae', '4.3.3.4.1', 'Optimiser l''offre existante', '<p>Amlioration de la qualit de service  amplitude et couverture du territoire, cadencement et augmentation des frquences, du maillage, ajustement de la localisation des arrts, adaptation de la desserte, confort des vhicules, en associant les AOM (des indicateurs chiffrs attestent de la qualit de loffre).</p>
