insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.4.4', 'cae', '4.3.3.4.4', 'Prendre des mesures pour assurer la fiabilit lors d''vnements climatiques extrmes, et la qualit des transports', '<p>En particulier le confort thermique et olfactif dans les transports en commun, en hiver et en t (ex : ombrage des stations de remisage).</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.3.4.5', 'cae', '4.3.3.4.5', 'Associer les principaux gnrateurs de dplacements  la rflexion, travailler en amont avec eux afin de les sensibiliser et les inciter  ajuster leur organisation et leurs horaires', '<p>Sans perdre de vue les ventuels risques d''une saturation du rseau de transports collectifs. La sensibilisation de ces acteurs porte notamment sur les enjeux associs  la mobilit et sur les bnfices qu''ils peuvent en tirer (amlioration de la ponctualit des salaris, diminution de la fatigue, etc.).</p>
', '', '', '', '', '', null, null, null);
