insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.4.1', 'cae', '4.3.1.4.1', 'Se saisir des outils rglementaires, avec des scnarios ambitieux (large primtre gographique, suffisamment de catgories de vhicules viss par les restrictions) et des moyens de contrle (respect par les usagers)', '', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_4.3.1.4.2', 'cae', '4.3.1.4.2', 'Crer une ou plusieurs aires pitonnes', '<p>Cest une zone ddie o seuls les tramways ont la priorit sur les pitons. Les cyclistes et les vhicules doivent rouler au pas. La circulation des vhicules motoriss est exceptionnelle, et conditionne  la desserte de la zone, de mme que le stationnement, y compris pour les rsidents. Un accs temporaire  la zone pitonne peut tre maintenu pour les vhicules motoriss des riverains et les livreurs qui disposent d''un badge pour passer les bornes automatiques.</p>
', '', '', '', '', '', null, null, null);
