insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.1.3.2', 'cae', '1.1.3.2', 'Connatre la vulnrabilit du territoire au changement climatique', '<p>Cette tude justifie des entretiens avec les acteurs du territoire (secteurs du btiment, de la sant, du tourisme, de lagriculture et de la fort, syndicats et gestionnaires dinfrastructures et rseaux deau, dnergie, de transport, etc.) et nest pas seulement ralise  partir de bibliographie.</p>
