insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.1.2.1', 'cae', '1.1.2.1', 'Formaliser les engagements, recenser et synthtiser les donnes et tudes existantes', '<p>Nota : Il est valu dans cette action la ralisation d''un bilan GES &quot;territoire&quot;. Ce type de bilan est obligatoire pour les EPCI&gt;50000 habitants (fin 2016) et &gt;20000 habitants (fin 2018).</p>
<p>Les collectivits obliges ne sont pas values sur cette action de base (1.1.2.1) mais uniquement sur les actions de mise en uvre (1.1.2.2  1.1.2.5).</p>
<p>Les communes et EPCI infrieurs  ces seuils dcidant de raliser ce type de bilan (ou uvrant pour la dclinaison communale du bilan de l''EPCI) peuvent tre valoriss sur la base.</p>
', '', '', '', '', '', null, 20.0, 'bases');
