insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.1.2', 'cae', '1.2.4.1.2', 'Prendre en compte les enjeux environnementaux dans le programme local de l''habitat (PLH)', '<p>La matrise des dplacements, la limitation de la consommation nergtique et des ressources naturelles (parmi lesquelles le foncier), la qualit de lair (intrieur et extrieur) sont bien prsentes dans le PLH (ou quivalent)  ct des proccupations sociales ou conomiques traditionnelles.</p>
