insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.3.2', 'cae', '1.2.4.3.2', 'Limiter ltalement urbain de la politique de lhabitat et du tertiaire', '<p>Fixer un nombre maximum de nouveaux logements (ou de surfaces de locaux tertiaires/commerciaux), encadrer leur typologie (collectif, individuel, intermdiaire) et leur localisation (% de logements/locaux tertiaire dans lenveloppe urbaine, % en renouvellement, % en extension).</p>
