insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.3.3', 'cae', '1.2.4.3.3', 'Dfinir des zones prfrentielles de dveloppement dans le Programme Local de l''Habitat cohrentes avec la mobilit durable', '<p>Le PLH (ou quivalent) dfinit des zones prfrentielles de dveloppement dans des zones bien desservies par les transports en communs et/ou dans des zones au trafic apais favorisant les modes actifs de prfrence  la voiture et/ou dans des zones o lexposition aux polluants atmosphriques est minimale ; ou facilitant le recours aux nergies renouvelables ou de rcupration, tout en prenant en compte l''exposition aux polluants atmosphriques.</p>
', '', '', '', '', '', null, null, null);
insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.4.3.4', 'cae', '1.2.4.3.4', 'Contextualiser et prioriser la politique de la collectivit par rapport aux enjeux forts/spcifiques du territoire', '<p>Exemples d''enjeux forts/spcifiques du territoire : habitat social, coproprits, rsidences secondaires, revitalisation des centres-bourg, lutte contre la vacance, bti  caractre patrimonial, etc.</p>
', '', '', '', '', '', null, null, null);
