insert into action_definition(action_id, referentiel, identifiant, nom, description, contexte, exemples, ressources, perimetre_evaluation, reduction_potentiel, points, pourcentage, categorie) values ('cae_1.2.1.4.2', 'cae', '1.2.1.4.2', 'Prvoir l''extension du rseau de chaleur et l''volution vers un mix nergtique suprieur  50 % dnergies renouvelables', '<p>En prsence dun rseau de chaleur existant, l''extension du rseau de chaleur est prvue ainsi que l''volution vers un mix nergtique suprieur  50 % dnergies renouvelables (si ce mix n''est pas encore atteint).</p>
